#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

ll N, Z;
ll ans;
// int A[10010], B[10010];
vector<short> pos, neg;
vector<short> sum;


int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);

    cin >> N;
    for (int i = 0; i < N; i++) {
        short tmp; cin >> tmp;
        if (tmp > 0) pos.push_back(tmp);
        else if (tmp < 0) neg.push_back(tmp);
        else Z++;
    }
    
    // sort(pos.begin(), pos.end());
    sort(neg.begin(), neg.end());
    if (Z) { // (n, 0, p)인 경우
        for (auto p : pos) ans += (upper_bound(neg.begin(), neg.end(), p*-1) - lower_bound(neg.begin(), neg.end(), p*-1)) * Z;
    } // O(N/2 * lg(N/2))
    if (Z >= 3) { // (0, 0, 0)인 경우
        ans += Z * (Z-1) * (Z-2) / 6;
    }

    for (int i = 0; i < neg.size()-1; i++) {
        for (int j = i+1; j < neg.size(); j++) {
            sum.push_back(neg[i] + neg[j]);
        }
    } // O(N^2)
    for (int i = 0; i < pos.size()-1; i++) {
        for (int j = i+1; j < pos.size(); j++) {
            sum.push_back(pos[i] + pos[j]);
        }
    } // O(N^2)
    sort(sum.begin(), sum.end()); // O(N^2lgN^2)
    for (auto p : pos) {
        short find = p*-1;
        ans += upper_bound(sum.begin(), sum.end(), find) - lower_bound(sum.begin(), sum.end(), find); // (n,n,p)인 경우
    } 
    for (auto n : neg) {
        short find = n*-1;
        ans += upper_bound(sum.begin(), sum.end(), find) - lower_bound(sum.begin(), sum.end(), find); // (p,p,n)인 경우
    }
    // sum.clear();

    // sort(sum.begin(), sum.end());

    cout << ans;
}
// O(N^2)까지는 통과 가능할 듯.
// 어떻게 해야 중복되는 걸 피할 수 있을까?
